import { useState } from 'react';
import './App.css';
import './normal.css';

function App() {

  const [input,setInput] = useState("");
  const [chatLog,setChatLog] = useState([{
    user: "gpt",
    message:""
  }])

  const handleSubmit = async(e) =>{
    e.preventDefault()
    setChatLog([...chatLog,{user:"me",message: `${input}`}])
    setInput("")
  }
  return (
    <div className="App">
      <aside className='sidemenu'>
      <div className='side-menu-button'>
        <span>+</span>
        New Chat
      </div>
      </aside>
      <section className='chatbox'>
        <div className='chat-log'>
           <ChatMessage message={message}/>
          <div className='chat-message chatgpt'>
            <div className='chat-message-center'>
            <div className='avatar chatgpt'>
            <svg xmlns="http://www.w3.org/2000/svg" width={48} height={41} >
    <title>{"Rectangle 5"}</title>
    <path fill="none" fillRule="evenodd" d="M0 0h48v1H0z" />
  </svg>
            </div>
            <div className='message'></div>
            </div>
          </div>
        </div>
        <div className='chat-input-holder'>
          <form onSubmit={handleSubmit}>
          <input rows="1" value={input} onChange={(e)=>setInput(e.target.value)} className='chat-input-textarea'></input>
          </form>
        </div>
      </section>
    </div>
  );
}

const ChatMessage = ({message}) => {
  return (
       <div className={`chat-message ${message.user === "gpt" && "chatgpt"}`}>
              <div className='chat-message-center'>
                <div className={`avatar ${message.user === "gpt" && "chatgpt"}`}></div>

                <div className='message'>
                  {message.message}
                </div>
              </div>
            </div>
  )
}
export default App;
